package LoopStatements;

public class doWhileLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int count =1;
		
		do{
			
			System.out.println("execute this block of code at least once");
			
			count++;
			
			
		}while(count<10);
		
		
			
			
			
			
		}

	}


