package LoopStatements;

public class whileLoopdemo {

	public static void main(String[] args){
		// TODO Auto-generated method stub
		
		int c = 90;
		
		while(c<101){
			
			System.out.println("The c value is less than " + c);
			
			c++;
			
			//break; //executes only one time
		}

		System.out.println("The c value is not equal to 101");
	}

}
